import React from 'react';
import styled from 'styled-components';

const Wrapper = styled('div')`
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
`;

const CircleCanvas = styled('canvas')`
    margin: 0 auto;
    // height: 100%;
`;

class CircleContainer extends React.Component{
    constructor(props) {
        super(props);
        this.state = { };
        this.canvasRef = React.createRef()
      }

    componentDidMount() {
        this.updateCanvas(this.canvasRef)
    }

    updateCanvas(canvas) {
        const ctx = canvas.current.getContext("2d");
        console.log(ctx.width/2);
        ctx.beginPath();
        ctx.arc(ctx.width/2, ctx.height/2, 150, 0, 2*Math.PI);
        ctx.stroke();
    }
    render() {
        return (
           <CircleCanvas width="600" height="600" ref={this.canvasRef} />
        )
    }

}

export default CircleContainer;
