import React, { useEffect, useState, useRef } from 'react';
import styled from 'styled-components';

const CircleWrap = styled('canvas')`
    margin: 0 auto;
    // height: 100%;
`;

const Circle = styled('div')`
    width: 200px;
    height: 200px;
    border-radius: 50%;
    border: 1px solid black;
    background: white;
    `;

