import React, { useEffect, useState, useRef } from 'react';
import styled from 'styled-components';

const Wrapper = styled('div')`
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
`;

const CircleCanvas = styled('canvas')`
    margin: 0 auto;
    // height: 100%;
`;

const initPage = () => {
    let audio = new Audio();
    let context = new (window.AudioContext || window.webkitAudioContext);
    let analyser = context.createAudioAnalyser();

    audio.src = "";
    let source = context.createMediaElementSource(audio);
    source.connect(analyser);
    analyser.connect(context.destination);

    let freqArr = new Uint8Array(analyser.frequencyBitCount);

    audio.play();
    animationLooper();
}

const CircleContainer = () => {
    let ref = useRef();
    let dpi = window.devicePixelRatio;

    useEffect(() => {
        let getCtx = ref.current;
        console.log(getCtx);
        setCtx(getCtx);
    }, [])
    const [ctx, setCtx] = useState('')

    let radius = 150;
    let center_x = ctx.width / 2;
    let center_y = ctx.height / 2;

    const animationLooper = () => {
        const drawCircle = () => {
            ctx.getContext('2d').imageSmoothingEnabled = false;
            ctx.getContext('2d').beginPath();
            ctx.getContext('2d').arc(center_x,center_y,radius,0,2*Math.PI);
            ctx.getContext('2d').stroke();
        }

        const drawBar = (x1, y1, x2, y2, width,frequency) => {

            let lineColor = "rgb(" + frequency + ", " + frequency + ", " + 205 + ")";

            ctx.getContext('2d').strokeStyle = lineColor;
            ctx.getContext('2d').lineWidth = width;
            ctx.getContext('2d').beginPath();
            ctx.getContext('2d').moveTo(x1,y1);
            ctx.getContext('2d').lineTo(x2,y2);
            ctx.getContext('2d').stroke();
        }

        const drawBars = () => {
            let bars = 200;
            for(let i=0; i < bars; i++) {
                let rads = Math.PI * 2 / bars;
                let bar_height = 100;
                let bar_width = 2;
                let x = center_x + Math.cos(rads * i) * (radius);
                let y = center_y + Math.sin(rads * i) * (radius);
                let x_end = center_x + Math.cos(rads * i)*(radius + bar_height);
                let y_end = center_y + Math.sin(rads * i)*(radius + bar_height);

                drawBar(x, y, x_end, y_end, bar_width);
            }
        }
    }

    return (
        <Wrapper>
            <CircleCanvas width="600" height="600" ref={ref}>
                {/* <Circle />
                <Bar /> */}
                {ctx && drawCircle()}
                {ctx && drawBars()}
            </CircleCanvas>
        </Wrapper>
    )
}

export default CircleContainer;
