import React, { useState, useEffect } from 'react';
import VisualDemo from './visualdemo';
import soundFile from '../music/it_comes_from_china.ogg';

const AudioDataContainer = () => {

    let frequencyBandArray = [...Array(25).keys()];
    const [audioData, setAudioData] = useState();

    let initializeAudioAnalyser = () => {
        const audioFile = new Audio();
        const audioContext = new AudioContext();
        const source = audioContext.createMediaElementSource(audioFile);
        const analyser = audioContext.createAnalyser();
        audioFile.src = soundFile;
        analyser.fftSize = 64;
        source.connect(audioContext.destination);
        source.connect(analyser);
        audioFile.play();
        setAudioData(analyser);
    }



    const getFrequencyData = (styleAdjuster) => {
        console.log(audioData);
        const bufferLength = audioData && audioData.frequencyBinCount;
        const amplitudeArray = new Uint8Array(bufferLength);
        audioData && audioData.getByteFrequencyData(amplitudeArray);
        styleAdjuster(amplitudeArray);
    }
    return (
        <div>
        <VisualDemo
            initializeAudioAnalyser={()=>initializeAudioAnalyser}
            frequencyBandArray={frequencyBandArray}
            getFrequencyData={()=>getFrequencyData}
            audioData={audioData}
        />
        </div>
    );
}

export default AudioDataContainer;
