import React, { useState, useEffect, useRef } from 'react';
import VisualDemo from './visualdemo';
import soundFile from '../music/it_comes_from_china.ogg';

const AudioDataContainer = () => {
    let context = new AudioContext();
    let source = context.createMediaElementSource(soundFile);
    let analyser = context.createAnalyser();
    source.connect(analyser);
    analyser.connect(context.destination);
    analyser.fftSize = 256;

    let bufferLength = analyser.frequencyBinCount;
    let dataArray = new Uint8Array(bufferLength);

    return(
        <VisualDemo  analyser={analyser} dataArray={dataArray} bufferLength={bufferLength} />
    )
}
export default AudioDataContainer;
