import React, { useState, useEffect, useRef } from 'react';
import VisualDemo from './visualdemo';
import soundFile from '../music/it_comes_from_china.ogg';

const AudioDataContainer = () => {

    // const [analyserState, setAnalyserState] = useState();
    // const [freqArray, setFreqArray] = useState([]);
    // const [audioState, setAudioState] = useState();

    const [isplaying, setplaying] = useState(false);


    function play() {
        setplaying(true);
        audio.play();
    }

    function pause() {
        setplaying(false);
        audio.pause();
    }

    let context = new AudioContext();
    let audio = new Audio(soundFile);
    let source = context.createMediaElementSource(audio);
    let analyser = context.createAnalyser();
    source.connect(analyser);
    analyser.connect(context.destination);
    analyser.fftSize = 256;
    let freq_array = new Uint8Array(analyser.frequencyBinCount);

    // let bufferLength = analyser.frequencyBinCount;
    // let dataArray = new Uint8Array(bufferLength);

    return(
        <>
        {console.log(analyser.getByteFrequencyData(freq_array))}

        <button onClick={() => play()}>Play Audio</button>
        <button onClick={() => pause()}>Pause Audio</button>
        <VisualDemo isPlaying={isplaying} analyser={analyser} dataArray={freq_array} bufferLength={analyser.frequencyBinCount} />
        </>
    )
}
export default AudioDataContainer;
