// import Paper from '@material-ui/core/Paper';
// import IconButton from '@material-ui/core/IconButton';
// import Tooltip from '@material-ui/core/Tooltip';
// import EqualizerIcon from '@material-ui/icons/Equalizer';
// import { makeStyles } from '@material-ui/core/styles';
import '../App.css';

const VisualDemo = ({...props}) => {
  console.log(document.querySelector("#canvas"));
  let Ref = useRef()
  const [divHeight, setHeight] = useState(0);

  let canvas = document.querySelector("#canvas");
  function renderFrame() {
    // props.analyser.getByteFrequencyData(props.dataArray);
    // console.log(canvas);
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    let ctx = canvas.current.getContext('2d');

    // let width = canvas.width;
    // let height = canvas.height;

    let barWidth = (2 / props.bufferLength);
    let barHeight;
    // let x = 0;
    // requestAnimationFrame(renderFrame);
    // x=0;
    for(let i=0; i < props.bufferLength; i++){
      barHeight = props.dataArray[i];
      setHeight(barHeight + (25 * (i/props.bufferLength)));

      // let g = 250 * (i/props.bufferLength);
      // let b = 50;
      // ctx.fillStyle = "rgb(" + r + "," + g + "," + b +")";
      // ctx.fillRect(x, height - barHeight, barWidth, barHeight);
      // x += barWidth + 1;
    }
  }  
  return (
    <canvas ref={Ref} height={200} width={750} id="canvas">
      {/* {props.isPlaying && renderFrame(Ref)} */}
      {/* {renderFrame(Ref.current)} */}
    </canvas>
  )
}

export default VisualDemo;
