import React, { useEffect, useState, useRef, Component } from 'react';
import styled from 'styled-components';

const Wrapper = styled('div')`
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
`;

const CircleCanvas = styled('canvas')`
    margin: 0 auto;
    // height: 100%;
`;

class CircleContainer extends Component(){
    constructor(props) {
        super(props);
        this.state = { };
      }

    componentDidMount() {
        this.updateCanvas()
    }

    updateCanvas() {
        const ctx = this.refs.canvas.getContext("2d");
        ctx.beginPath();
        ctx.arc(ctx.width/2, ctx.height/2, 150, 0, 2*Math.PI);
        ctx.stroke();
    }
    render() {
        return (
           <CircleCanvas ref="canvas" />
        )
    }

}

export default CircleContainer;
