//     ctx.fill();

//     ctx.font = "10px";
//     ctx.fillText(label,x + 10,y);
// }

const CircleContainer = () => {
    let ref = useRef();
    const [ctx, setCtx] = useState('')
    useEffect(() => {
        console.log(ctx);
        let getCtx = ref.current.getContext('2d');
        setCtx(getCtx);
    }, [])

    const drawCircle = () => {
            ctx.beginPath();
            ctx.arc(100, 100, 100, 0, 2 * Math.PI);
            ctx.stroke();
    }

    const drawBars = (angle,distance,label) => {
        var x = 100 + 100 * Math.cos(-angle*Math.PI/180) * distance;
        var y = 100 + 100 * Math.sin(-angle*Math.PI/180) * distance;

        ctx.beginPath();
        ctx.arc(x, y, 4, 0, 2 * Math.PI);
        ctx.fill();

        ctx.font = "10px";
        ctx.fillText(label,x + 10,y);
    }
    return (
        <CircleWrap id="my-canvas" ref={ref}>
            {/* <Circle />
            <Bar /> */}
            {drawCircle()}
        </CircleWrap>
    )
}

export default CircleContainer;
