import React, { useEffect, useState, useRef, createContext } from 'react';
import styled from 'styled-components';

const Wrapper = styled('div')`
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
`;

const CircleCanvas = styled('canvas')`
    margin: 0 auto;
    // height: 100%;
`;



const CircleContainer = () => {
    let ref = useRef();
    let audio = new Audio();
    let context = new (window.AudioContext || window.webkitAudioContext)();
    let analyser = context.createAnalyser();
    let freqArr = new Uint8Array(analyser.frequencyBinCount);
    audio.src = "/it_came_from_china.mp3";
    let source = context.createMediaElementSource(audio);

    const [ctx, setCtx] = useState()
    const Context = createContext();

    useEffect(() => {
        let getCtx = ref.current;
        setCtx(getCtx.getContext("2d"));
    })

    let radius = 150;
    let center_x = ctx && ctx.width / 2;
    let center_y = ctx && ctx.height / 2;

    const animationLooper = (ctx) => {
            let bars = 200;
            // ctx.imageSmoothingEnabled = false;
            ctx && ctx.beginPath();
            ctx.arc(center_x,center_y,radius,0,2*Math.PI);
            ctx.stroke();
            console.log('Here', ctx);
            analyser.getByteFrequencyData(freqArr);

            // for(let i=0; i < bars; i++) {
            //     let rads = Math.PI * 2 / bars;
            //     let bar_height = 100;
            //     let bar_width = 2;
            //     let x = center_x + Math.cos(rads * i) * (radius);
            //     let y = center_y + Math.sin(rads * i) * (radius);
            //     let x_end = center_x + Math.cos(rads * i)*(radius + bar_height);
            //     let y_end = center_y + Math.sin(rads * i)*(radius + bar_height);
            //     drawBar(x, y, x_end, y_end, bar_width, freqArr[i], ctx && ctx);
            // }

            window.requestAnimationFrame(animationLooper);

    }

    const drawBar = (x1, y1, x2, y2, width,frequency, ctx) => {
        console.log(ctx)
        let lineColor = "rgb(" + frequency + ", " + frequency + ", " + 205 + ")";
        ctx.strokeStyle = lineColor;
        ctx.lineWidth = width;
        ctx.beginPath();
        ctx.moveTo(x1,y1);
        ctx.lineTo(x2,y2);
        ctx.stroke();
    }
    const initPage = (ctx) => {
        source.connect(analyser);
        analyser.connect(context.destination);
        // audio.play();
        console.log('init');
        ctx && animationLooper(ctx);
}



    return (
        <Wrapper>
            <Context.CircleCanvas value={ctx} width="600" height="600" ref={ref}>
                {ctx && initPage(ctx)}
                {/* <Circle />
                <Bar /> */}
                {/* {ctx && drawCircle()} */}
                {/* {ctx && drawBars()} */}
                {/* {animationLooper()} */}
            </Context.CircleCanvas>
        </Wrapper>
    )
}

export default CircleContainer;
