import React from 'react';
import styled from 'styled-components';

const Wrapper = styled('div')`
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
`;

const CircleCanvas = styled('canvas')`
    margin: 0 auto;
    // height: 100%;
`;

class CircleContainer extends React.Component{
    constructor(props) {
        super(props);
        this.state = { };
        this.canvasRef = React.createRef()
      }

    componentDidMount() {
        this.updateCanvas(this.canvasRef)
        let audio = new Audio();
        let context = new (window.AudioContext || window.webkitAudioContext)();
        let analyser = context.createAnalyser();
        let freqArr = new Uint8Array(analyser.frequencyBitCount);
        audio.src = "/it_came_from_china.mp3";
        let source = context.createMediaElementSource(audio);
        source.connect(analyser);
        analyser.connect(context.destination);
        // audio.play();
        // animationLooper(analyser, freqArr);
    }

    updateCanvas(canvas) {
        const ctx = canvas.current.getContext("2d");
        ctx.beginPath();
        ctx.arc(300, 300, 150, 0, 2*Math.PI);
        ctx.stroke();
    }
    render() {
        return (
           <CircleCanvas width="600" height="600" ref={this.canvasRef} />
        )
    }

}

export default CircleContainer;
