import React from 'react';
import styled from 'styled-components';
import song from '../music/it_comes_from_china.ogg';

const Wrapper = styled('div')`
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
`;

const CircleCanvas = styled('canvas')`
    margin: 0 auto;
    // height: 100%;
`;

class CircleContainer extends React.Component{
    constructor(props) {
        super(props);
        this.audio = new Audio(song);
        // this.audio.muted = "muted"
        this.state = {
            analyser: '',
            freqArray: [],
            audio: {}
         };
    }

    togglePlay(audio) {
        if(audio.paused) {
            audio.play().then(res=>console.log(res))
            .catch(err => console.log('error', err));
          } else {
            audio.paused();
          }
    }

    render() {
        return (
            <Wrapper>
                {/* {console.log(this.audio)} */}
               <button onClick={this.togglePlay(this.audio)}>Play/Pause</button>
            </Wrapper>
        )
    }

}

export default CircleContainer;
