import React from 'react';
import styled from 'styled-components';

const Wrapper = styled('div')`
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
`;

const CircleCanvas = styled('canvas')`
    margin: 0 auto;
    // height: 100%;
`;

class CircleContainer extends React.Component{
    constructor(props) {
        super(props);
        this.state = { };
        this.canvasRef = React.createRef()
      }

    componentDidMount() {
        this.updateCanvas(this.canvasRef)
        let audio = new Audio();
        let context = new (window.AudioContext || window.webkitAudioContext)();
        let analyser = context.createAnalyser();
        let freqArr = new Uint8Array(analyser.frequencyBitCount);
        audio.src = "/it_came_from_china.mp3";
        let source = context.createMediaElementSource(audio);
        source.connect(analyser);
        analyser.connect(context.destination);
        // audio.play();
        // animationLooper(analyser, freqArr);
    }

    drawBar(x1, y1, x2, y2, width, ctx) {
        // let lineColor = "rgb(" + frequency + ", " + frequency + ", " + 205 + ")";
        // ctx.strokeStyle = lineColor;
        ctx.lineWidth = width;
        ctx.beginPath();
        ctx.moveTo(x1,y1);
        ctx.lineTo(x2,y2);
        ctx.stroke();
    }

    updateCanvas(canvas) {
        const bars = 200;
        const radius = 150;
        const center_x = 300;
        const center_y = 300;
        const ctx = canvas.current.getContext("2d");
        ctx.beginPath();
        ctx.arc(center_x, center_y, 150, 0, 2*Math.PI);
        ctx.stroke();

        for(let i=0; i < bars; i++) {
            let rads = Math.PI * 2 / bars;
            let bar_height = 100;
            let bar_width = 2;
            let x = center_x + Math.cos(rads * i) * (radius);
            let y = center_y + Math.sin(rads * i) * (radius);
            let x_end = center_x + Math.cos(rads * i)*(radius + bar_height);
            let y_end = center_y + Math.sin(rads * i)*(radius + bar_height);
            this.drawBar(x, y, x_end, y_end, bar_width, ctx);
        }
        window.requestAnimationFrame(this.updateCanvas);

    }


    render() {
        return (
            <Wrapper>
                <CircleCanvas width="600" height="600" ref={this.canvasRef} />
            </Wrapper>
        )
    }

}

export default CircleContainer;
