import React from 'react';
import styled from 'styled-components';

const CircleWrap = styled('canvas')`
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100%;
`;

const Circle = styled('div')`
    width: 200px;
    height: 200px;
    border-radius: 50%;
    border: 1px solid black;
    background: white;
`;

const Bar = styled('div')`
    height: 100px;
    width: 0px;
    border: 1px solid black;
`;

let c = document.getElementById("my-canvas")
let ctx = c.getContext("2d");

const drawCircle = () => {
    ctx.beginPath();
    ctx.arc(100, 100, 100, 0, 2 * Math.PI);
    ctx.stroke();
}

const drawBars = (angle,distance,label) => {
    var x = 100 + 100 * Math.cos(-angle*Math.PI/180) * distance;
    var y = 100 + 100 * Math.sin(-angle*Math.PI/180) * distance;

    ctx.beginPath();
    ctx.arc(x, y, 4, 0, 2 * Math.PI);
    ctx.fill();

    ctx.font = "10px";
    ctx.fillText(label,x + 10,y);
}

const CircleContainer = () => {
    return (
        <CircleWrap id="my-canvas">
            <Circle />
            <Bar />
        </CircleWrap>
    )
}

export default CircleContainer;
